mapper.select:
select

from mro_quduan_info qi inner join mro_quduan_base qb on qi.qid=qb.id
where qi.id = #{id,jdbcType=INTEGER}

select

from mro_quduan_info qi inner join mro_quduan_base qb on qi.qid=qb.id


        and qi.qid=#{qid,jdbcType=INTEGER}


        and qi.time=#{time,jdbcType=TIMESTAMP}

select
qi.id
from mro_quduan_info qi inner join mro_quduan_base qb on qi.qid = qb.id


        and qb.xid=#{xid,jdbcType=INTEGER}


        and qb.cid=#{cid,jdbcType=INTEGER}


        and qi.time between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}

select

from mro_quduan_info qi inner join mro_quduan_base qb on qi.qid = qb.id


        and qb.xid=#{xid,jdbcType=INTEGER}


        and qb.cid=#{cid,jdbcType=INTEGER}


        and DATE(qi.time) = DATE(#{time,jdbcType=TIMESTAMP})

SELECT
FROM mro_quduan_info qi INNER JOIN mro_quduan_base qb ON qi.qid = qb.id
and qb.xid=#{xid,jdbcType=INTEGER}
and qb.cid=#{cid,jdbcType=INTEGER}
and DATE(qi.time) = DATE(#{time,jdbcType=TIMESTAMP})
GROUP BY qi.qid
